import React, { Component } from 'react'

export default class UpdateGiftForm extends Component {
  state = {
    title: "",
    image_link: "",
    description: "",
    due_date: ""
  }

  setFormData = () => {
    if (this.props.giftLists.length) {
      const {
        title,
        image_link,
        description,
        due_date,
        ...otherData
      } = this.props.giftLists.find(giftList => {
        return giftList.id === parseInt(this.props.giftListId)
      })
      this.setState({
        title,
        image_link,
        description,
        due_date,
      })
    }
  }

  componentDidMount() {
    this.setFormData();
  }

  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value })
    //console.log(this.state);
  }

  componentDidUpdate(prevProps) {
    if (prevProps.giftLists !== this.props.giftLists) {
      this.setFormData();
    }
  }

  render() {
    const { title, image_link, description, due_date } = this.state;
    const { show, handleClose, giftFormData, handleChange } = props;
    const showHideClassName = show ? "modal display-block" : "modal display-none";

    return (
      <div className={showHideClassName}>
        < form className="modal-main" onSubmit={(e) => {
          e.preventDefault();
          props.updateGift(giftFormData.id, giftFormData);
        }}>
          <label htmlFor="item">Gift</label>
          <input
            type="text"
            name="item"
            id="item"
            value={giftFormData.item}
            onChange={handleChange}
          />
          <label htmlFor="description">Description</label>
          <input
            type="text"
            name="description"
            id="description"
            value={giftFormData.description}
            onChange={handleChange}
          />
          <label htmlFor="image_link">Image</label>
          <input
            type="text"
            name="image_link"
            id="image_link"
            value={giftFormData.image_link}
            onChange={handleChange}
          />
          <label htmlFor="price">Price</label>
          <input
            type="text"
            name="price"
            id="price"
            value={giftFormData.price}
            onChange={handleChange}
          />
          <label htmlFor="location">Location</label>
          <input
            type="text"
            name="location"
            id="location"
            value={giftFormData.location}
            onChange={handleChange}
          />
          <label htmlFor="proposed_purchase_date">proposed_purchase_date</label>
          <input
            type="text"
            name="proposed_purchase_date"
            id="proposed_purchase_date"
            value={giftFormData.proposed_purchase_date}
            onChange={handleChange}
          />
          <label htmlFor="actual_purchase_date">actual_purchase_date</label>
          <input
            type="text"
            name="actual_purchase_date"
            id="actual_purchase_date"
            value={giftFormData.actual_purchase_date}
            onChange={handleChange}
          />
          <button onClick={handleClose}>Add Gift</button>
        </form >
      </div >
    )
  }

}
